import fetchDataFromApi from './fetchDataFromApi';
import DataItemComponent from './DataItemComponent';
import React, { useState, useEffect } from 'react';

const DataListComponent = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    // Replace 'https://api.example.com/data' with the actual API URL
    const apiUrl = 'https://api.example.com/data';
  
    try {
      const response = await fetch(apiUrl);
  
      if (!response.ok) {
        throw new Error(`Failed to fetch data. Status: ${response.status}`);
      }
  
      const result = await response.json();
      setData(result);
    } catch (error) {
      console.error("Error fetching data:", error.message);
      // Handle the error as needed, e.g., display an error message to the user
    }
  };
  

  return (
    <div>
      <h2>Data List</h2>
      <button onClick={fetchData}>Fetch Data</button>
      <ul>
        {/* Мапінг по масиву записів та створення окремого компонента для кожного запису */}
        {data.map((item, index) => (
          <DataItemComponent key={index} item={item} />
        ))}
      </ul>
    </div>
  );
};

export default DataListComponent;
