async function fetchDataFromApi(apiUrl, apiKey) {
    try {
        const response = await fetch(`${apiUrl}&appid=${apiKey}`);
      const data = await response.json();
      return data; 
    } 
    catch (error) {
      console.error("Error fetching data:", error);
      return []; 
    }
  }

  export default fetchDataFromApi;
