import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';  // Додайте цей імпорт
import { setCurrentDate } from './actions';  // Додайте цей імпорт
import CurrentDate from './CurrentDate';
import CurrentTime from './CurrentTime';

const DateTime = () => {
  const dispatch = useDispatch();

  const [currentDateTime, setCurrentDateTime] = useState(new Date());

  const tick = () => {
    const newDate = new Date();
    setCurrentDateTime(newDate);
    dispatch(setCurrentDate(newDate));  // Оновіть дату за допомогою нового екшна
  };

  useEffect(() => {
    const intervalId = setInterval(tick, 1000);

    return () => clearInterval(intervalId);
  }, [dispatch]);  // Додайте dispatch до залежностей useEffect

  return (
    <div>
      <CurrentDate currentDate={currentDateTime} />
      <CurrentTime currentTime={currentDateTime} />
    </div>
  );
};

export default DateTime;
