import React from 'react';
import { getId } from './helpers';

const MenuItem = ({ text, url }) => {
  const menuItemId = getId('menu-item');

  function openLink(url) {
        if (url && !url.startsWith(window.location.origin)) {
            window.open(url, '_blank');
        } else {
            window.location.href = url;
        }
    }
  return (
    <li id={menuItemId}>
      {url ? (
        // <a href={url} target={url && !url.startsWith(window.location.origin) ? window.open(url, '_blank') : window.location.href = url} rel="noopener noreferrer">
        //   {text}
        // </a>


        <a href={url} target="_blank" rel="noopener noreferrer" onclick="openLink('{url}')">
            {text}
        </a>
      ) : (
        <span>{text}</span>
      )}
    </li>
  );
};

export default MenuItem;
