import React, { useState, useEffect } from 'react';
import fetchDataFromApi from './fetchDataFromApi';
import DataItemComponent from './DataItemComponent';

const DataListComponent = () => {
  const [data, setData] = useState([]);
  const apiKey = '196bc45aabe31f92312e2d3a5411366a';

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=Kyiv&appid=${apiKey}&units=metric`;
    const result = await fetchDataFromApi(apiUrl, apiKey);
    console.log(result);
    setData(result);
  };

  return (
    <div>
      <h2>Weather Information</h2>
      <button onClick={fetchData}>Fetch Weather</button>
      {data.length > 0 ? (
        <ul>
          {data.map((item, index) => (
            <DataItemComponent key={index} item={item} />
          ))}
        </ul>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default DataListComponent;
