// SideBar.js
import React from 'react';
import './SideBar.css';
import Menu from './Menu';
import { useSelector, useDispatch } from 'react-redux';

const SideBar = () => {
  const dispatch = useDispatch();
  const sideBarMenuList = useSelector((state) => state.sideBarMenuList);

  const updateSideBarMenu = (newMenuList) => {
    dispatch({ type: 'SET_SIDEBAR_MENU', payload: newMenuList });
  };

  // Приклад використання функції для оновлення меню
  const handleMenuUpdate = () => {
    const newMenuList = [
      { text: 'New Item 1', url: '/new-item-1' },
      { text: 'New Item 2', url: '/new-item-2' },
    ];

    updateSideBarMenu(newMenuList);
  };

  return (
    <div className="sidebar">
      <Menu list={sideBarMenuList} />
      <button onClick={handleMenuUpdate}>Update Menu</button>
    </div>
  );
};

export default SideBar;
