// Logo.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';

const Logo = () => {
  const dispatch = useDispatch();
  const imagePath = useSelector((state) => state.imagePath);

  const handleImagePathChange = (newImagePath) => {
    dispatch({ type: 'SET_IMAGE_PATH', payload: newImagePath });
  };

  return (
    <div>
      <a href="http://localhost:3000/">
        <img
          className="logo_img"
          src={imagePath}
          alt="Logo"
          onClick={() => handleImagePathChange('новий_шлях_до_картинки.jpg')}
        />
      </a>
    </div>
  );
};

export default Logo;
